-- =============================================
-- Rain Forest Experiences CR — Blog Table
-- Run this in the Supabase SQL Editor
-- =============================================

-- Create blog articles table
CREATE TABLE IF NOT EXISTS rain_forest_exp_blog (
  id              UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  slug            TEXT UNIQUE NOT NULL,
  title           TEXT NOT NULL,
  title_es        TEXT NOT NULL,
  excerpt         TEXT NOT NULL,
  excerpt_es      TEXT NOT NULL,
  content         TEXT NOT NULL,
  content_es      TEXT NOT NULL,
  image           TEXT NOT NULL,
  date            DATE NOT NULL,
  read_time       INTEGER NOT NULL DEFAULT 5,
  related_tours   TEXT[] NOT NULL DEFAULT '{}',
  active          BOOLEAN NOT NULL DEFAULT true,
  created_at      TIMESTAMPTZ DEFAULT now(),
  updated_at      TIMESTAMPTZ DEFAULT now()
);

-- Indexes
CREATE INDEX IF NOT EXISTS idx_rain_forest_exp_blog_slug ON rain_forest_exp_blog (slug);
CREATE INDEX IF NOT EXISTS idx_rain_forest_exp_blog_active ON rain_forest_exp_blog (active) WHERE active = true;
CREATE INDEX IF NOT EXISTS idx_rain_forest_exp_blog_date ON rain_forest_exp_blog (date DESC);

-- Enable RLS (Row Level Security)
ALTER TABLE rain_forest_exp_blog ENABLE ROW LEVEL SECURITY;

-- Public read-only policy
CREATE POLICY "Public can read rain_forest_exp_blog"
  ON rain_forest_exp_blog FOR SELECT
  USING (active = true);

